    public function fetch_markets($params = array ()) {
        $response = $this->publicGetMarketSymbols (array_merge(array(
            'api_key' => $this->apiKey,
        ), $params));
        $markets = $this->safe_value($response, 'data');
        if (!$markets) {
            throw new ExchangeError($this->id . ' fetchMarkets got an unrecognized response');
        }
        $result = array();
        for ($i = 0; $i < count($markets); $i++) {
            $market = $markets[$i];
            $parts = explode('_', $market['symbol']);
            $numParts = is_array($parts) ? count($parts) : 0;
            $baseId = $parts[0];
            $quoteId = $parts[1];
            if ($numParts > 2) {
                $baseId = $parts[0] . '_' . $parts[1];
                $quoteId = $parts[2];
            }
            $base = $this->safe_currency_code($baseId);
            $quote = $this->safe_currency_code($quoteId);
            $precision = array(
                'amount' => 8,
                'price' => $market['price_scale'],
            );
            $limits = array(
                'amount' => array(
                    'min' => $this->safe_float($market, 'min_amount'),
                    'max' => $this->safe_float($market, 'max_amount'),
                ),
                'price' => array(
                    'min' => null,
                    'max' => null,
                ),
                'cost' => array(
                    'min' => null,
                    'max' => null,
                ),
            );
            $result[] = array(
                'id' => $market['symbol'],
                'symbol' => $base . '/' . $quote,
                'base' => $base,
                'quote' => $quote,
                'baseId' => $baseId,
                'quoteId' => $quoteId,
                'info' => $market,
                'active' => true,
                'maker' => $this->safe_float($market, 'maker_fee_rate'),
                'taker' => $this->safe_float($market, 'taker_fee_rate'),
                'precision' => $precision,
                'limits' => $limits,
            );
        }
        return $result;
    }

    public function fetch_balance($params = array ()) {
        $this->load_markets();
        $request = array();
        $response = $this->privateGetAccountInfo (array_merge($request, $params));
        $result = array( 'info' => $response );
        $balances = $this->safe_value($response, 'data', array());
        $currencyIds = is_array($balances) ? array_keys($balances) : array();
        for ($i = 0; $i < count($currencyIds); $i++) {
            $currencyId = $currencyIds[$i];
            $code = $this->safe_currency_code($currencyId);
            $account = $this->account();
            $account['free'] = $this->safe_float($balances[$currencyId], 'available');
            $account['used'] = $this->safe_float($balances[$currencyId], 'frozen');
            $result[$code] = $account;
        }
        return $this->parse_balance($result);
    }

