    public function parse_ohlcv($ohlcv, $market = null, $timeframe = '1m', $since = null, $limit = null) {
        // they return array( Timestamp, Volume, Close, High, Low, Open )
        return [
            intval ($ohlcv[0]),   // t
            floatval ($ohlcv[1]), // o
            floatval ($ohlcv[2]), // c
            floatval ($ohlcv[3]), // h
            floatval ($ohlcv[4]), // l
            floatval ($ohlcv[5]), // v
            // floatval ($ohlcv[6]), // a -- leaving this out as it is not in CCXT OHLCV structure
        ];
    }

