    public function parse_trade($trade, $market = null) {
        $dateStr = $this->safe_value($trade, 'tradeTime');
        $timestamp = null;
        if ($dateStr !== null) {
            $timestamp = $this->parse_date($dateStr . '  GMT+8');
        }
        // take either of orderid or orderId
        $price = $this->safe_float($trade, 'trade_price');
        $amount = $this->safe_float($trade, 'trade_quantity');
        $type = $this->safe_string($trade, 'trade_type');
        $cost = null;
        if ($price !== null) {
            if ($amount !== null) {
                $cost = $price * $amount;
            }
        }
        $symbol = null;
        if ($market !== null) {
            $symbol = $market['symbol'];
        }
        return array(
            'id' => null,
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'order' => null,
            'type' => null,
            'side' => $this->parse_order_side($type),
            'takerOrMaker' => null,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'fee' => null,
        );
    }

