    public function parse_order_side($side) {
        $sides = array(
            'BID' => 'buy',
            'ASK' => 'sell',
        );
        return $this->safe_string($sides, $side, $side);
    }

    public function parse_order_status($status) {
        $statuses = array(
            'NEW' => 'open',
            'FILLED' => 'closed',
            'PARTIALLY_FILLED' => 'open', // partial closed
            'CANCELED' => 'canceled', // partial closed
            'PARTIALLY_CANCELED' => 'canceled', // partial canceled
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_order($order, $market = null) {
        // Different API endpoints returns $order info in different format...
        // with different fields $filled->
        $id = $this->safe_string($order, 'id');
        $symbol = null;
        $marketId = $this->safe_string($order, 'symbol');
        if (is_array($this->markets_by_id) && array_key_exists($marketId, $this->markets_by_id)) {
            $market = $this->markets_by_id[$marketId];
        }
        if ($market !== null) {
            $symbol = $market['symbol'];
        }
        $timestamp = $this->safe_string($order, 'create_time');
        $status = $this->parse_order_status($this->safe_string($order, 'state'));
        $side = $this->parse_order_side($this->safe_string($order, 'type'));
        $price = $this->safe_float($order, 'price');
        $amount = $this->safe_float($order, 'quantity');
        $filled = $this->safe_float($order, 'deal_quantity');
        $remaining = null;
        if (($filled !== null) && ($amount !== null)) {
            $remaining = $amount - $filled;
        }
        return array(
            'id' => $id,
            'datetime' => $this->iso8601($timestamp),
            'timestamp' => $timestamp,
            'status' => $status,
            'symbol' => $symbol,
            'type' => 'limit',
            'side' => $side,
            'price' => $price,
            'cost' => null,
            'amount' => $amount,
            'filled' => $filled,
            'remaining' => $remaining,
            'trades' => null,
            'fee' => array(
                'cost' => null,
                'currency' => null,
                'rate' => null,
            ),
            'info' => $order,
        );
    }

    public function create_order($symbol, $type, $side, $amount, $price = null, $params = array ()) {
        if ($type === 'market') {
            throw new ExchangeError($this->id . ' allows limit orders only');
        }
        $this->load_markets();
        $request = array(
            'symbol' => $this->market_id($symbol),
            'price' => $price,
            'quantity' => $amount,
            'order_type' => 'LIMIT_ORDER',
            'trade_type' => ($side === 'buy') ? 'BID' : 'ASK',
        );
        $response = $this->privatePostOrderPlace (array_merge($request, $params));
        return array_merge(array(
            'id' => $this->safe_string($response, 'data'),
            'timestamp' => $this->milliseconds(),
            'status' => 'open',
            'type' => 'limit',
            'price' => $price,
            'amount' => $amount,
            'info' => $response,
        ), $request);
    }

    public function fetch_open_orders($symbol = null, $since = null, $limit = null, $params = array ()) {
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' fetchOpenOrders() requires a $symbol argument');
        }
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'symbol' => $market['id'],
        );
        $response = $this->privateGetOrderOpenOrders (array_merge($request, $params));
        return $this->parse_orders($response['data'], $market, $since, $limit);
    }

    public function cancel_order($id, $symbol = null, $params = array ()) {
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' cancelOrder requires $symbol argument');
        }
        $this->load_markets();
        $request = array(
            'order_ids' => $id,
        );
        return $this->privateDeleteOrderCancel (array_merge($request, $params));
    }

    public function sign($path, $api = 'public', $method = 'GET', $params = array (), $headers = null, $body = null) {
        $url = $this->urls['api'][$api] . $this->implode_params($path, $params);
        $requestTime = (string) $this->milliseconds();
        $query = $params;
        if ($api === 'public') {
            if ($query) {
                $url .= '?' . $this->urlencode($query);
            }
        } else {
            $this->check_required_credentials();
            $toBeSigned = '';
            if ($method === 'POST') {
                $body = $this->json($params);
                $toBeSigned = $body;
            } else {
                $toBeSigned = $this->rawencode($this->keysort($query));
                if ($query) {
                    $url .= '?' . $toBeSigned;
                }
            }
            $signature = $this->hmac($this->encode($this->apiKey . $requestTime . $toBeSigned), $this->encode($this->secret), 'sha256');
            $headers = array(
                'ApiKey' => $this->apiKey,
                'Request-Time' => $requestTime,
                'Signature' => $signature,
                'Content-Type' => 'application/json',
            );
        }
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }

    public function handle_errors($code, $reason, $url, $method, $headers, $body, $response, $requestHeaders, $requestBody) {
        if ($response === null) {
            return;
        }
        $resultString = $this->safe_string($response, 'result', '');
        if ($resultString !== 'false') {
            return;
        }
        $errorCode = $this->safe_string($response, 'code');
        $message = $this->safe_string($response, 'message', $body);
        if ($errorCode !== null) {
            $feedback = $this->safe_string($this->errorCodeNames, $errorCode, $message);
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $errorCode, $feedback);
        }
    }
