    async def fetch_markets(self, params={}):
        response = await self.publicGetMarketSymbols(self.extend({
            'api_key': self.apiKey,
        }, params))
        markets = self.safe_value(response, 'data')
        if not markets:
            raise ExchangeError(self.id + ' fetchMarkets got an unrecognized response')
        result = []
        for i in range(0, len(markets)):
            market = markets[i]
            parts = market.symbol.split('_')
            numParts = len(parts)
            baseId = parts[0]
            quoteId = parts[1]
            if numParts > 2:
                baseId = parts[0] + '_' + parts[1]
                quoteId = parts[2]
            base = self.safe_currency_code(baseId)
            quote = self.safe_currency_code(quoteId)
            precision = {
                'amount': 8,
                'price': market.price_scale,
            }
            limits = {
                'amount': {
                    'min': self.safe_float(market, 'min_amount'),
                    'max': self.safe_float(market, 'max_amount'),
                },
                'price': {
                    'min': None,
                    'max': None,
                },
                'cost': {
                    'min': None,
                    'max': None,
                },
            }
            result.append({
                'id': i,
                'symbol': base + '_' + quote,
                'base': base,
                'quote': quote,
                'baseId': baseId,
                'quoteId': quoteId,
                'info': market,
                'active': True,
                'maker': market['maker_fee_rate'],
                'taker': market['taker_fee_rate'],
                'precision': precision,
                'limits': limits,
            })
        return result

    async def fetch_balance(self, params={}):
        await self.load_markets()
        request = {
            'api_key': self.apiKey,
            'req_time': self.seconds(),
        }
        response = await self.privateGetAccountInfo(self.extend(request, params))
        result = {'info': response}
        currencyIds = list(response.data.keys())
        for i in range(0, len(currencyIds)):
            currencyId = currencyIds[i]
            code = self.safe_currency_code(currencyId)
            account = self.account()
            account['free'] = self.safe_float(response[currencyId], 'available')
            account['used'] = self.safe_float(response[currencyId], 'frozen')
            result[code] = account
        return self.parse_balance(result)

